sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	var caller = g_form.getValue('caller_id');
	var ga = new GlideAjax('getDataFromServer'); //class name in script include
	ga.addParam('sysparm_name', 'getUserdata');//function name in script include
	ga.addParam('sysparm_user_id', caller);
	ga.getXML(returnDataFromeScriptInclude);

	function returnDataFromeScriptInclude(response) {
		var info = response.responseXML.documentElement.getAttribute('answer');
		g_form.setValue('u_email', info);
	}


	//Type appropriate comment here, and begin script below

}
