var getCallerDetails = Class.create();
getCallerDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getuserInformation: function () {
		gs.log('fechinformation::start');
		var user = this.getParameter('sysparm_user');
		gs.log('fechinformation:: user'+user);
		var gr = new GlideRecord('sys_user');
		gs.log('fechinformation:: gr');
		gr.addQuery('sys_id', user);
		gs.log('fechinformation:: query');
		gr.query();
		gs.log('fechinformation::');
		gr.next();
		gs.log('fechinformation::');

		var jsonobj = {};

		gs.log('fechinformation:: jsonobj');

		jsonobj.email = gr.getValue('email');
		gs.log('fechinformation:: '+ jsonobj.email);
			jsonobj.phonenumber = gr.getValue('phonenumber');
			gs.log('fechinformation::'+ jsonobj.phonenumber );
			jsonobj.manager =gr.getValue('manager');
			gs.log('fechinformation::'+jsonobj.manager);
			jsonobj.manageremail =gr.getValue('manageremail');
			gs.log('fechinformation::'+ jsonobj.manageremail);
			return JSON.stringify(jsonobj);

	},

	type: 'getCallerDetails'
});
